import { useEffect, useState } from "react";
import Msg from "../../components/Msg";
import "../../styles/pages/messaging/OneMsgPage.scss";
import axios from "axios";
import { useParams } from "react-router-dom";

function OneMsgPage() {
  const { id } = useParams();
  const [msgs, setMsgs] = useState([]) as any[];
  const fetchMsgs = () => {
    axios
      .get(`/.../${id}`, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `token ${localStorage.getItem("token")}`,
        },
      })
      .then((response) => {
        setMsgs(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    fetchMsgs();
  }, []);

  return (
    <>
      <div className="one-msg-page-main-container">
        <div className="one-msg-page-title">مکالمه</div>
        <div className="one-msg-page-list">
          {msgs.map((msg: any) => {
            return (
              <>
                <Msg
                  msgTime={msg.msgTime}
                  msgBody={msg.msgBody}
                  msgTitle={msg.msgTitle}
                  senderName={msg.senderName}
                />
                <div></div>
                <hr />
                <div></div>
              </>
            );
          })}
          <Msg
            msgTime="12:22 AM"
            msgBody=""
            msgTitle="salam"
            senderName="maher"
          />
          <div></div>
          <hr />
          <div></div>
          <Msg
            msgTime="12:22 AM"
            msgBody=""
            msgTitle="salam"
            senderName="maher"
          />
          <div></div>
          <hr />
          <div></div>
          <Msg
            msgTime="12:22 AM"
            msgBody=""
            msgTitle="salam"
            senderName="maher"
          />
          <div></div>
          <hr />
          <div></div>
          <Msg
            msgTime="12:22 AM"
            msgBody=""
            msgTitle="salam"
            senderName="maher"
          />
        </div>
      </div>
    </>
  );
}

export default OneMsgPage;
